from gridenc import gridenclib 


def test_grid_init():
    assert gridenclib.init_grid(5,1234) == [
        ['\x0c', '%', 'o', 'a', 'm'], ['_', 'f', '7', '\t', 'n'],
        ['r', 'Z', 'K', 'd', 'g'],
        ['e', '$', '\x0b', ' ', '['],
        ['\n', '?', 'C', 'u', 'I'],
        ['y', 'c', '1', '~', 'U'],
        ['s', '}', 't', 'z', '\r'],
        ['l', '&', '`', '(', 'k'],
        [':', 'N', '"', '6', 'R'],
        ['L', 'H', '\\', 'E', '*'],
        ['/', '0', 'h', ';', 'q'],
        ['b', 'Y', 'F', '>', '+'],
        ['J', 'v', 'x', '2', 'P'],
        ['A', 'W', 'Q', 'j', '8'],
        ['X', '|', 'B', '.', 'G'],
        ['3', '!', '<', 'p', '='],
        ['#', 'w', 'T', ',', 'S'],
        ['V', '9', 'M', ']', 'i'],
        ['5', '^', '@', ')', "'"],
        ['4', '{', 'O', 'D', '-']]
    assert gridenclib.init_grid(10,2008) == [['\x0c', '%', 'o', 'a', 'm', '_', 'f', '7', '\t', 'n'], ['r', 'Z', 'K', 'd', 'g', 'e', '$', '\x0b', ' ', '['], ['\n', '?', 'C', 'u', 'I', 'y', 'c', '1', '~', 'U'], ['s', '}', 't', 'z', '\r', 'l', '&', '`', '(', 'k'], [':', 'N', '"', '6', 'R', 'L', 'H', '\\', 'E', '*'], ['/', '0', 'h', ';', 'q', 'b', 'Y', 'F', '>', '+'], ['J', 'v', 'x', '2', 'P', 'A', 'W', 'Q', 'j', '8'], ['X', '|', 'B', '.', 'G', '3', '!', '<', 'p', '='], ['#', 'w', 'T', ',', 'S', 'V', '9', 'M', ']', 'i'], ['5', '^', '@', ')', "'", '4', '{', 'O', 'D', '-']]

