__nodes = Variable(contract='masternodes', name='nodes')
__votes = Hash(default_value=False, contract='masternodes', name='votes')
__total_votes = Variable(contract='masternodes', name='total_votes')
__types = Variable(contract='masternodes', name='types')
__registration_fee = Variable(contract='masternodes', name='registration_fee')
__pending_registrations = Hash(default_value=False, contract='masternodes',
    name='pending_registrations')
__pending_leave = Hash(default_value=False, contract='masternodes', name=
    'pending_leave')


def ____(genesis_nodes: list, genesis_registration_fee: int):
    __nodes.set(genesis_nodes)
    __types.set(['add_member', 'remove_member', 'change_registration_fee',
        'reward_change', 'dao_payout', 'stamp_cost_change', 'change_types'])
    __total_votes.set(0)
    __registration_fee.set(genesis_registration_fee)


@__export('masternodes')
def propose_vote(type_of_vote: str, arg: Any):
    assert ctx.caller in __nodes.get(), 'Only nodes can propose new votes'
    if type_of_vote == 'add_member':
        assert __pending_registrations[arg
            ] == True, 'Member must have pending registration'
    assert type_of_vote in __types.get(), 'Invalid type'
    proposal_id = __total_votes.get() + 1
    __votes[proposal_id] = {'yes': 1, 'no': 0, 'type': type_of_vote, 'arg':
        arg, 'voters': [ctx.caller], 'finalized': False}
    __total_votes.set(proposal_id)
    if len(__votes[proposal_id]['voters']) >= len(__nodes.get()) // 2:
        if not __votes[proposal_id]['finalized']:
            __finalize_vote(proposal_id)
    return proposal_id


@__export('masternodes')
def vote(proposal_id: int, vote: str):
    assert ctx.caller in __nodes.get(), 'Only nodes can vote'
    assert __votes[proposal_id], 'Invalid proposal'
    assert __votes[proposal_id]['finalized'
        ] == False, 'Proposal already finalized'
    assert vote in ['yes', 'no'], 'Invalid vote'
    assert ctx.caller not in __votes[proposal_id]['voters'], 'Already voted'
    cur_vote = __votes[proposal_id]
    cur_vote[vote] += 1
    cur_vote['voters'].append(ctx.caller)
    __votes[proposal_id] = cur_vote
    if len(__votes[proposal_id]['voters']) >= len(__nodes.get()) // 2:
        if not __votes[proposal_id]['finalized']:
            __finalize_vote(proposal_id)
    return cur_vote


def __finalize_vote(proposal_id: int):
    cur_vote = __votes[proposal_id]
    if cur_vote['yes'] > cur_vote['no']:
        if cur_vote['type'] == 'add_member':
            __nodes.set(__nodes.get() + [cur_vote['arg']])
        elif cur_vote['type'] == 'remove_member':
            __nodes.set([node for node in __nodes.get() if node != cur_vote
                ['arg']])
        elif cur_vote['type'] == 'reward_change':
            rewards.set_value(new_value=cur_vote['arg'])
        elif cur_vote['type'] == 'dao_payout':
            currency.transfer_from_dao(args=cur_vote['arg'])
        elif cur_vote['type'] == 'stamp_cost_change':
            stamp_cost.set_value(new_value=cur_vote['arg'])
        elif cur_vote['type'] == 'change_registration_fee':
            __registration_fee.set(cur_vote['arg'])
        elif cur_vote['type'] == 'change_types':
            __types.set(cur_vote['arg'])
    cur_vote['finalized'] = True
    __votes[proposal_id] = cur_vote
    return cur_vote


